import React, { useState, useEffect } from 'react';
import api from '../../services/api';

const MovimentacaoForm = () => {
  const [armeiro, setArmeiro] = useState('');
  const [matriculaArmeiro, setMatriculaArmeiro] = useState('');
  const [data, setData] = useState('');
  const [hora, setHora] = useState('');
  const [tipo, setTipo] = useState('emprestimo');
  const [armaId, setArmaId] = useState('');
  const [quantidadeBalas, setQuantidadeBalas] = useState('');
  const [calibre, setCalibre] = useState('');
  const [quantidadeCarregadores, setQuantidadeCarregadores] = useState('');
  const [guardaId, setGuardaId] = useState('');
  const [porteGuarda, setPorteGuarda] = useState('');
  const [matriculaGuarda, setMatriculaGuarda] = useState('');
  const [justificativa, setJustificativa] = useState('');
  const [message, setMessage] = useState('');

  const [armas, setArmas] = useState([]);
  const [guardas, setGuardas] = useState([]);

  useEffect(() => {
    const fetchDados = async () => {
      try {
        const [armasRes, guardasRes] = await Promise.all([
          api.get('/armas'),
          api.get('/guardas')
        ]);
        setArmas(armasRes.data);
        setGuardas(guardasRes.data);
      } catch (error) {
        console.error("Erro ao buscar dados para movimentação:", error);
      }
    };
    fetchDados();
  }, []);

  // Ao selecionar um guarda, preenche automaticamente porte e matrícula
  const handleGuardaChange = (e) => {
    const selectedGuardId = e.target.value;
    setGuardaId(selectedGuardId);
    const selectedGuard = guardas.find(guard => guard.id.toString() === selectedGuardId);
    if (selectedGuard) {
      setPorteGuarda(selectedGuard.numeracao_porte);
      setMatriculaGuarda(selectedGuard.matricula);
    } else {
      setPorteGuarda('');
      setMatriculaGuarda('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/movimentacoes', {
        movimentacao: {
          armeiro,
          matricula_armeiro: matriculaArmeiro,
          data,
          hora,
          tipo,
          arma_id: armaId,
          quantidade_balas: quantidadeBalas,
          calibre,
          quantidade_carregadores: quantidadeCarregadores,
          guarda_id: guardaId,
          porte_guarda: porteGuarda,
          matricula_guarda: matriculaGuarda,
          justificativa: justificativa
        }
      });
      setMessage('Movimentação cadastrada com sucesso!');
      // Opcional: limpar os campos após o cadastro
    } catch (error) {
      setMessage('Erro ao cadastrar movimentação.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Cadastrar Movimentação</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Armeiro" value={armeiro} onChange={(e) => setArmeiro(e.target.value)} required />
        <input type="text" placeholder="Matrícula do Armeiro" value={matriculaArmeiro} onChange={(e) => setMatriculaArmeiro(e.target.value)} required />
        <input type="date" value={data} onChange={(e) => setData(e.target.value)} required />
        <input type="time" value={hora} onChange={(e) => setHora(e.target.value)} required />
        <select value={tipo} onChange={(e) => setTipo(e.target.value)} required>
          <option value="emprestimo">Empréstimo</option>
          <option value="devolucao">Devolução</option>
        </select>
        <div>
          <label>Arma: </label>
          <select value={armaId} onChange={(e) => setArmaId(e.target.value)} required>
            <option value="">Selecione uma arma</option>
            {armas.map(arma => (
              <option key={arma.id} value={arma.id}>
                {arma.modelo} - Registro: {arma.registro}
              </option>
            ))}
          </select>
        </div>
        <input type="text" placeholder="Quantidade de Balas" value={quantidadeBalas} onChange={(e) => setQuantidadeBalas(e.target.value)} required />
        <input type="text" placeholder="Calibre" value={calibre} onChange={(e) => setCalibre(e.target.value)} required />
        <input type="text" placeholder="Quantidade de Carregadores" value={quantidadeCarregadores} onChange={(e) => setQuantidadeCarregadores(e.target.value)} required />
        <div>
          <label>Guarda: </label>
          <select value={guardaId} onChange={handleGuardaChange} required>
            <option value="">Selecione um guarda</option>
            {guardas.map(guarda => (
              <option key={guarda.id} value={guarda.id}>
                {guarda.nome_completo}
              </option>
            ))}
          </select>
        </div>
        {/* Esses campos serão preenchidos automaticamente */}
        <input type="text" placeholder="Porte do Guarda" value={porteGuarda} onChange={(e) => setPorteGuarda(e.target.value)} required />
        <input type="text" placeholder="Matrícula do Guarda" value={matriculaGuarda} onChange={(e) => setMatriculaGuarda(e.target.value)} required />
        <textarea placeholder="Justificativa (caso haja discrepância na devolução)" value={justificativa} onChange={(e) => setJustificativa(e.target.value)} />
        <button type="submit">Cadastrar Movimentação</button>
      </form>
    </div>
  );
};

export default MovimentacaoForm;
