import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const EquipeList = () => {
  const [equipes, setEquipes] = useState([]);
  const [message, setMessage] = useState('');

  const fetchEquipes = async () => {
    try {
      const response = await api.get('/equipes');
      setEquipes(response.data);
    } catch (error) {
      console.error("Erro ao buscar equipes:", error);
    }
  };

  useEffect(() => {
    fetchEquipes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/equipes/${id}`);
      setMessage('Equipe removida com sucesso.');
      fetchEquipes();
    } catch (error) {
      setMessage('Erro ao remover equipe.');
      console.error("Erro:", error);
    }
  };

  const handleUpdate = async (id, currentNome) => {
    const novoNome = window.prompt("Digite o novo nome para a equipe:", currentNome);
    if (!novoNome || novoNome.trim() === '') return;
    try {
      await api.put(`/equipes/${id}`, { equipe: { nome: novoNome } });
      setMessage('Equipe atualizada com sucesso.');
      fetchEquipes();
    } catch (error) {
      setMessage('Erro ao atualizar equipe.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Equipes</h2>
      {message && <p>{message}</p>}
      <ul>
        {equipes.map(equipe => (
          <li key={equipe.id}>
            {equipe.nome}
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleUpdate(equipe.id, equipe.nome)}
            >
              Atualizar
            </button>
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleDelete(equipe.id)}
            >
              Deletar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EquipeList;
