import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const ArmaList = () => {
  const [armas, setArmas] = useState([]);
  const [message, setMessage] = useState('');

  const fetchArmas = async () => {
    try {
      const response = await api.get('/armas');
      setArmas(response.data);
    } catch (error) {
      console.error("Erro ao buscar armas:", error);
    }
  };

  useEffect(() => {
    fetchArmas();
  }, []);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/armas/${id}`);
      setMessage('Arma removida com sucesso.');
      fetchArmas();
    } catch (error) {
      setMessage('Erro ao remover arma.');
      console.error("Erro:", error);
    }
  };

  const handleUpdate = async (id, currentModelo, currentEmprestada) => {
    const novoModelo = window.prompt("Digite o novo modelo para a arma:", currentModelo);
    if (!novoModelo || novoModelo.trim() === '') return;
    
    // Solicita o novo status para 'emprestada'
    const novoEmprestadaInput = window.prompt("A arma está emprestada? (Digite true ou false):", currentEmprestada ? "true" : "false");
    let novoEmprestada = false;
    if (novoEmprestadaInput && novoEmprestadaInput.toLowerCase() === "true") {
      novoEmprestada = true;
    } else if (novoEmprestadaInput && novoEmprestadaInput.toLowerCase() === "false") {
      novoEmprestada = false;
    } else {
      // Se o usuário inserir um valor inválido, não prossegue
      setMessage("Valor inválido para emprestada. Use true ou false.");
      return;
    }

    try {
      await api.put(`/armas/${id}`, { arma: { modelo: novoModelo, emprestada: novoEmprestada } });
      setMessage('Arma atualizada com sucesso.');
      fetchArmas();
    } catch (error) {
      setMessage('Erro ao atualizar arma.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Armas</h2>
      {message && <p>{message}</p>}
      <ul>
        {armas.map(arma => (
          <li key={arma.id}>
            {arma.modelo} - Registro: {arma.registro} - Emprestada: {arma.emprestada ? "Sim" : "Não"}
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleUpdate(arma.id, arma.modelo, arma.emprestada)}
            >
              Atualizar
            </button>
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleDelete(arma.id)}
            >
              Deletar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ArmaList;
