import React, { useState } from 'react';
import api from '../../services/api';

const ArmaForm = () => {
  const [modelo, setModelo] = useState('');
  const [registro, setRegistro] = useState('');
  const [emprestada, setEmprestada] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/armas', { arma: { modelo, registro, emprestada } });
      setMessage(`Arma "${response.data.modelo}" criada com sucesso!`);
      setModelo('');
      setRegistro('');
      setEmprestada(false);
    } catch (error) {
      setMessage('Erro ao criar arma.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Cadastrar Arma</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Modelo da Arma"
          value={modelo}
          onChange={(e) => setModelo(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Registro da Arma"
          value={registro}
          onChange={(e) => setRegistro(e.target.value)}
          required
        />
        <div>
          <label>
            <input
              type="checkbox"
              checked={emprestada}
              onChange={(e) => setEmprestada(e.target.checked)}
            />
            Emprestada?
          </label>
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
};

export default ArmaForm;
