import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const MovimentacaoList = () => {
  const [movimentacoes, setMovimentacoes] = useState([]);
  const [message, setMessage] = useState('');

  const fetchMovimentacoes = async () => {
    try {
      const response = await api.get('/movimentacoes');
      setMovimentacoes(response.data);
    } catch (error) {
      console.error("Erro ao buscar movimentações:", error);
    }
  };

  useEffect(() => {
    fetchMovimentacoes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/movimentacoes/${id}`);
      setMessage('Movimentação removida com sucesso.');
      fetchMovimentacoes();
    } catch (error) {
      setMessage('Erro ao remover movimentação.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Movimentações</h2>
      {message && <p>{message}</p>}
      <ul>
        {movimentacoes.map(mov => (
          <li key={mov.id}>
            <strong>Tipo:</strong> {mov.tipo} - <strong>Data:</strong> {mov.data} - <strong>Hora:</strong> {mov.hora}<br />
            <strong>Armeiro:</strong> {mov.armeiro} (Matrícula: {mov.matricula_armeiro})<br />
            <strong>Guarda:</strong> {mov.guarda ? mov.guarda.nome_completo : "N/A"} (Porte: {mov.porte_guarda}, Matrícula: {mov.matricula_guarda})<br />
            <strong>Arma:</strong> {mov.arma ? mov.arma.modelo : "N/A"} (Registro: {mov.arma ? mov.arma.registro : "N/A"})<br />
            <strong>Balas:</strong> {mov.quantidade_balas} - <strong>Calibre:</strong> {mov.calibre} - <strong>Carregadores:</strong> {mov.quantidade_carregadores}<br />
            <strong>Justificativa:</strong> {mov.justificativa || "Nenhuma"}<br />
            <button style={{ marginLeft: '10px' }} onClick={() => handleDelete(mov.id)}>
              Deletar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MovimentacaoList;
