import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../../services/api';
import Swal from 'sweetalert2';

const containerStyle = {
  maxWidth: '400px',
  margin: '80px auto',
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '8px',
  boxShadow: '2px 2px 10px rgba(0,0,0,0.1)',
  backgroundColor: '#fff'
};

const headerStyle = {
  textAlign: 'center',
  marginBottom: '20px'
};

const inputStyle = {
  padding: '10px',
  marginBottom: '15px',
  width: '100%',
  border: '1px solid #ccc',
  borderRadius: '4px'
};

const labelStyle = {
  display: 'block',
  marginBottom: '5px',
  fontWeight: 'bold'
};

const buttonStyle = {
  width: '100%',
  padding: '10px',
  backgroundColor: '#343a40',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  marginBottom: '10px'
};

const linkStyle = {
  display: 'block',
  textAlign: 'center',
  marginTop: '10px',
  textDecoration: 'none',
  color: '#343a40'
};

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("LoginForm: handleSubmit chamado. E-mail:", email);
    try {
      // Exemplo: ajuste conforme seu endpoint de login; aqui espera receber um token.
      const response = await api.post('/login', { email, senha });
      console.log("Resposta do backend:", response.data);
      // Armazena o token no localStorage (ajuste a chave conforme sua implementação)
      localStorage.setItem('authToken', response.data.token);
      Swal.fire({
        title: 'Sucesso!',
        text: 'Login realizado com sucesso.',
        icon: 'success',
        confirmButtonText: 'OK'
      });
      // Redireciona para a área protegida (por exemplo, para "/unidades")
      navigate('/unidades');
    } catch (error) {
      console.error("Erro no login:", error);
      let errorMsg = 'Erro ao realizar login.';
      if (error.response && error.response.data && error.response.data.error) {
        errorMsg += ' ' + error.response.data.error;
      }
      Swal.fire({
        title: 'Erro!',
        text: errorMsg,
        icon: 'error',
        confirmButtonText: 'OK'
      });
    }
  };

  return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        <h2>Login</h2>
      </div>
      <form onSubmit={handleSubmit}>
        <label style={labelStyle}>E-mail:</label>
        <input 
          type="email" 
          placeholder="Digite seu e-mail" 
          style={inputStyle}
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        <label style={labelStyle}>Senha:</label>
        <input 
          type="password" 
          placeholder="Digite sua senha" 
          style={inputStyle}
          value={senha} 
          onChange={(e) => setSenha(e.target.value)} 
          required 
        />
        <button type="submit" style={buttonStyle}>Entrar</button>
      </form>
      <Link to="/password-reset" style={linkStyle}>Esqueci minha senha</Link>
      <Link to="/cadastro" style={linkStyle}>Cadastrar novo usuário</Link>
    </div>
  );
};

export default LoginForm;
