import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const GuardaForm = () => {
  const [nomeCompleto, setNomeCompleto] = useState('');
  const [matricula, setMatricula] = useState('');
  const [numeracaoPorte, setNumeracaoPorte] = useState('');
  const [equipeId, setEquipeId] = useState('');
  const [equipes, setEquipes] = useState([]);
  const [message, setMessage] = useState('');

  // Buscar as equipes para popular o dropdown
  useEffect(() => {
    const fetchEquipes = async () => {
      try {
        const response = await api.get('/equipes');
        setEquipes(response.data);
      } catch (error) {
        console.error("Erro ao buscar equipes:", error);
      }
    };
    fetchEquipes();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/guardas', { 
        guarda: { 
          nome_completo: nomeCompleto, 
          matricula, 
          numeracao_porte: numeracaoPorte, 
          equipe_id: equipeId 
        }
      });
      setMessage(`Guarda "${response.data.nome_completo}" cadastrado com sucesso!`);
      setNomeCompleto('');
      setMatricula('');
      setNumeracaoPorte('');
      setEquipeId('');
    } catch (error) {
      setMessage('Erro ao cadastrar guarda.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Cadastrar Guarda</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome Completo"
          value={nomeCompleto}
          onChange={(e) => setNomeCompleto(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Matrícula"
          value={matricula}
          onChange={(e) => setMatricula(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Numeração do Porte"
          value={numeracaoPorte}
          onChange={(e) => setNumeracaoPorte(e.target.value)}
          required
        />
        <div>
          <label>Equipe:</label>
          <select 
            value={equipeId} 
            onChange={(e) => setEquipeId(e.target.value)} 
            required
          >
            <option value="">Selecione uma equipe</option>
            {equipes.map((equipe) => (
              <option key={equipe.id} value={equipe.id}>
                {equipe.nome}
              </option>
            ))}
          </select>
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
};

export default GuardaForm;
