import React, { useEffect, useState } from 'react';
import api from '../../services/api';

const UnidadeList = () => {
  const [unidades, setUnidades] = useState([]);
  const [message, setMessage] = useState('');

  const fetchUnidades = async () => {
    try {
      const response = await api.get('/unidades');
      setUnidades(response.data);
    } catch (error) {
      console.error("Erro ao buscar unidades:", error);
    }
  };

  useEffect(() => {
    fetchUnidades();
  }, []);

  const handleDelete = async (id) => {
    try {
      await api.delete(`/unidades/${id}`);
      setMessage('Unidade removida com sucesso.');
      fetchUnidades();
    } catch (error) {
      setMessage('Erro ao remover unidade.');
      console.error("Erro:", error);
    }
  };

  const handleUpdate = async (id, currentNome) => {
    const novoNome = window.prompt("Digite o novo nome para a unidade:", currentNome);
    if (!novoNome || novoNome.trim() === '') return;
    try {
      await api.put(`/unidades/${id}`, { unidade: { nome: novoNome } });
      setMessage('Unidade atualizada com sucesso.');
      fetchUnidades();
    } catch (error) {
      setMessage('Erro ao atualizar unidade.');
      console.error("Erro:", error);
    }
  };

  return (
    <div>
      <h2>Lista de Unidades</h2>
      {message && <p>{message}</p>}
      <ul>
        {unidades.map(unidade => (
          <li key={unidade.id}>
            {unidade.nome}
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleUpdate(unidade.id, unidade.nome)}
            >
              Atualizar
            </button>
            <button 
              style={{ marginLeft: '10px' }}
              onClick={() => handleDelete(unidade.id)}
            >
              Deletar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UnidadeList;
